// parentheses verification:
// verify if the given string is valid for counting and matching of '[' : ']', '{' : '}' 

function jsTreeDataVerify($jsTreeData)  {  
    $res=0;
    $firstPos=0; // where the problem appears first time
    //counting:
    if(substr_count($jsTreeData, "[") !== substr_count($jsTreeData, "]")) $res = 1; 
    if(substr_count($jsTreeData, "{") !== substr_count($jsTreeData, "}")) $res += 2;
    
    //pair matching:
    $curBrackets=0;
    $squBrackets=0;
    $innerCurBrackets=0;
    $innerSquBrackets=0;
    $p=0;
    $len=strlen($jsTreeData);
    while($p<$len){
        if($jsTreeData[$p] === '{') { $curBrackets++; $innerSquBrackets=0; if($squBrackets>0) $innerCurBrackets++; }
        else if($jsTreeData[$p] === '}') { $curBrackets--; if($innerSquBrackets!==0) {$res += 4; if($firstPos===0) {$firstPos = $p; break;}}}
        else if($jsTreeData[$p] === '[') { $squBrackets++; $innerCurBrackets=0; if($curBrackets>0) $innerSquBrackets++; }
        else if($jsTreeData[$p] === ']') { $squBrackets--; if($innerCurBrackets!==0) {$res += 8; if($firstPos===0) {$firstPos = $p; break;}}}
        $p++;
    }
    if($curBrackets!==0)  $res += 16;
    if($squBrackets!==0)  $res += 32;
    
    if($res>0) {
		echo "<br>jsTreeDataVerify() returns ".$res."<br>";
		echo "<br>check first position ".$firstPos."<br>";
	}
    return $res;     
}
