$MAX_DEPTH = 600;
$JsonTag = array();
$JsonTree = array();
$hierarchyHasChild = array();
$nTagsOnLevel = array(); // the no-> of tags on this $level
$Hierarchies = array("html", "body", "head", "div", "ul", "ol", "li", "tt");
////////////////////////////////////////////////////////////////////////////////
class Tag {
    public $name;
    public $attrs;
    public $value;
    public $level;
    public $isHierarchy;
    public $hasChild;

    public function  __construct(){
        $this->name = null;
        $this->attrs = null;
        $this->value = null;
        $this->level = 0;
        $this->isHierarchy = false;
        $this->hasChild = true;
    }
}

// this type of $tag can have child-nodes if its $value == null:
function isHierarchical($tag){
    if($tag==="html" || $tag==="head" ||
        $tag==="body" || strpos("div", $tag) === 0 ||
        $tag==="ul"   || $tag==="ol" || $tag==="li")
        return true;

    return false;
}

function readHTMLFile($htmlfile){
    $s="";
    $handle = @fopen($htmlfile, "r"); 
    while (($buffer = fgets($handle, 4096)) !== false) {
        $s = $s.trim($buffer);
    } 
    fclose($handle);
     
    return $s;
} 

 
// only check if <html>, <head>, <body> are missed;
//if yes, we think the html file is fatal error.
function simpleValidator($html){ //// !!
	if(strpos($html, "<html") === -1){
		print("critical error 1 of html: tag  html is missed in html file. abort.<br>");
		return false;
	}
	if(strpos($html, "<body") === -1){
		print("critical error 2 of html: tag  body is missed in html file. abort.<br>");
		return false;
	}
	if(strpos($html, "<head") === -1){
		print("critical error 3 of html: tag  head is missed in html file. abort.<br>");
		return false;
	}
	return true;
}

/*
 * check if some </div> are missed; -- e.g., index_moa.html
 * if yes, then add them before </body>
 * we also check other main hierarchical tags like head, body, ul, li, ol, tt...
 */
function insertMissedHierarchies($html){  //// !!
	if(!simpleValidator($html))	return null;
	 
	//special case: <li will be mis-counted when <link appears
	$linkCount = substr_count($html, "<link");
	global $Hierarchies;
	foreach($Hierarchies as $sub) {
		$missed = substr_count($html, "<".$sub) - substr_count($html, "</".$sub.">");
		if($sub==="li") $missed -= $linkCount;
		if($missed < 0){
			print("tag=".$sub.", missed=".$missed);
			print("critical html error 1: '<".$sub."' is missed in html file. abort.<br>");
			return null;
		}
		else if($missed===0) continue;
		else print("warning: tag=".$sub.", '</".$sub.">' missed=".$missed."<br>");
		
		$insert = "";
		while($missed>0){
			$insert = $insert."</".$sub.">";
			$missed--;
		} 
		if($sub==="html") $pos = strlen($html);
		else if($sub==="body") $pos = stripos($html, "</html>");
		else if($sub==="head") $pos = stripos($html, "<body");
		else $pos = stripos($html, "</body>");
		 
		//System.out.println("sub="+sub+", pos="+pos+", html="+html);
		$html = substr($html, 0, $pos).$insert.substr($html, $pos, strlen($html)-$pos);
	}
	 
	return $html;
}

	
// intput: $attri=name="keywords" content="Movies,Films,Animated"
// output: "attributes:..." in JSON format
function parseAttr($attri){
    $attrs = array();
    $len=strlen($attri);
    /*
	if($len>200) { //// !!
		$aJson = str_replace('/', '\/', $attri);
		array_push($attrs, $aJson);
		return $attrs;
	}*/
    $len=strlen($attri);
    $p=0;
    while($p<$len){
        $p1=$p;
        //want to see two '"':
        //e->g., lang="en"
        while($p<$len && $attri[$p]!='"')$p++;
        $aJson = '"' . substr($attri, $p1, $p-1-$p1) . '":';
        $p1=$p;
        $p++;
        while($p<$len && substr($attri, $p, 1)!='"') $p++;
        if($p+1>$len) {  
			$aJson =$aJson . substr($attri, $p1, $len-$p1); //// !!
		}
		else  $aJson = $aJson . substr($attri, $p1, $p+1-$p1);
        $aJson = str_replace('/', '\/', $aJson); 
        array_push($attrs, $aJson);  
        $p++;
        while($p<$len && (substr($attri, $p, 1)==' ' || substr($attri, $p, 1)=='/')) $p++;
    }
     
    return $attrs;
}

// build JsonTag[] based on html content       
function htmlParse2JSON($html){
    global $hierarchyHasChild;
    global $nTagsOnLevel; // the no. of tags on this $level
    global $JsonTag;    
    global $MAX_DEPTH;
    // init:
    for($i=0; $i<$MAX_DEPTH; $i++){
        $nTagsOnLevel[$i] = 0;
        $hierarchyHasChild[$i] = false;
    }
    
    //echo "len=".$len."<br>";
    
	if(insertMissedHierarchies($html) === null) return false; //failed  //// !!
	
    $p=0;
    $h=0;
    $level = 0;
	$html = str_replace("'", "\'", $html);
	$len = strlen($html); //// !!
    while($p<$len){
        // $tag in '<.->tag..>':
        $tag = new Tag();
        while($p<$len && substr($html, $p, 1)!='<') $p++;
        $p1 = $p;
        while($p<$len && substr($html, $p, 1)!='>') $p++;
        $p2 = $p+1;

        //echo "p = ".$p."<br>";
    
        // tagName and attributes in $tag:
        $p3=$p1+1;
        while($p3<$p2 && substr($html, $p3, 1) !=' ' && substr($html, $p3, 1) !='>') $p3++;
        if($p1+1>=$p3) continue; ///////////
        $tag->name = substr($html, $p1+1, $p3-$p1-1);

        // three special $tag types: <!...>,  </...> and second <a> (1):
        if(strpos($tag->name, "!") === 0) {
            if($p1+2<$len && substr($html, $p1+2, 1)==='-') { // if "<!--" look forward until find "-->"
                $p = $p1+2;
                while($p<$len && !(substr($html, $p, 1)==='-' && substr($html, $p+1, 1)==='>')) $p++;
                $p++;
            }
            continue;
        }

        //echo "p := ".$p."<br>";
        
        if(strpos($tag->name, "/") === 0) {
            if(isHierarchical(substr($tag->name, 1, strlen($tag->name)-1))){
				if($h<0) continue;  //// !!
                if($hierarchyHasChild[$h]){
                    $nTagsOnLevel[$level--]=0;
                    $nTagsOnLevel[$level--]=0;
                    //echo "level-- level--<br>";
                }
                $hierarchyHasChild[$h--] = false;
                //echo "h--<br>";
            }
            //$p++;
            continue;
        }

        $tag->isHierarchy = isHierarchical($tag->name);

        if($p3+1<$p) $attri = substr($html, $p3+1, $p-$p3-1);
        else $attri = null;
        if($attri != null && $attri === "") $attri = null;        
        if($attri!=null) $tag->attrs = parseAttr($attri);
        $p1=$p2;
        
        // $tag->value in > ..'$tag->value'.. <
        while($p<$len && substr($html, $p, 1)!='<') $p++;
        $p2=$p;
        if($p1<=$p2) $tag->value = trim(substr($html, $p1, $p2-$p1));
        if($tag->value!=null && $tag->value === "") $tag->value = null;
        
        //echo "p :=: ".$p."<br>";   
        
        $tag->hasChild = (substr($html, $p+1, 1)!='/'); // like <body></body> --> noChild; <body><div> --> hasChild
        
        // three special $tag types: <!...>,  </...> and second <a> (2):
        if($tag->name === "a" && $tag->value==null) { continue; }
        
        //echo "tagname=".$tag->name."; p=".$p."<br>"; 
           
        //ensemble the $tag object:
        if($nTagsOnLevel[$level]===0) {
            $level++;
            $nTagsOnLevel[$level]=1;
            $tag->level = $level;
            $level++;
            $nTagsOnLevel[$level]=0;
            //echo "level++ level++<br>";
        }
        else {
            $nTagsOnLevel[$level]++;
            $tag->level = $level;
            $level++;
            $nTagsOnLevel[$level]=0;
            //echo "level++ <br>";
        }
        
        //echo "<br>tag.isHierar=" .$tag->isHierarchy. ";<br>tag.hasChild=". $tag->hasChild."<br><br>";
        
        if($tag->isHierarchy && !$tag->hasChild){
            $hierarchyHasChild[++$h] = false;
            //echo "++h child=false <br>";
        }

        if($tag->isHierarchy && $tag->hasChild){
            $hierarchyHasChild[++$h] = true;
            //echo "++h child=true <br>";
        }
        else {
            $nTagsOnLevel[$level]=0;
            $level--;
            //echo "level-- <br>";
        }
        
        //print("level=".$level.", h=".$h.", hierarHasChild[h]=".$hierarchyHasChild[$h] );
        //echo ",  tag->level=".$tag->level."<br><br>";  
        
        $JsonTag[] = $tag; 
    } 
    //echo "count(JsonTag)=" . count($JsonTag);
    return true; //// !!
}

/*
	Input:   JsonTag[] tag object array
	Process: parse attributes to tag objects 
	Output:  JsonTree[] tag object array ( JsonTag[] can be considered as part of JsonTree[])
*/
function createJsonTreeData(){
    global $JsonTag;
    global $JsonTree;
    foreach($JsonTag as $tag){
        $tag1 = new Tag();
        $tag1->name = $tag->name;
        $tag1->hasChild = $tag->hasChild;
        $tag1->level = $tag->level;
        $JsonTree[] =$tag1;

        if($tag->attrs!=null){
            $tag1->hasChild = true;
            foreach($tag->attrs as $a){
                $tag2 = new Tag(); 
                $tag2->name = str_replace('"', "", $a); 
                $tag2->level = $tag->level+2;
                $tag2->hasChild = false;
                $JsonTree[] =$tag2;
            }
        }
        if($tag->value!=null){
            $tag1->hasChild = true;
            $tag2 = new Tag();
            $tag2->name = "text:" . $tag->value;
            $tag2->level = $tag->level+2;
            $tag2->hasChild = false;
            $JsonTree[] = $tag2;
        }
    }
}

/* 
	Input: JsonTree[] tag object array
	Output: jsTree data (a string used to build a JSON tree with $.jsTree() function)

	jsTree data is like:
	 
	{text:'html', children:[ 
		{text:'head', children:[ 
		    {text:'meta', children:[ 
		        {text:':""charset":"utf-8"'}, 
		    {text:'title', children:[ 
		        {text:':text:jsTree test'} ]}, 
		    {text:'link', children:[ 
		        {text:':""rel":"style"'}, 
		        {text:':""href":"dist/themes"'} 
		    ]}
		  ]}, 
		
		{text:'body', children:[ {text:'div', children:[ {text:':""id":"jstree"'}, {text:'ul', children:[ {text:'li', children:[ {text:':text:Root node 1'}, {text:'ul', children:[ {text:'li', children:[ {text:':""id":"child_node_1"'}, {text:':text:Child node 1'} ]}, {text:'li', children:[ {text:':text:Child node 2'} ]} ]} ]}, {text:'li', children:[ {text:':text:Root node 2'} ]} ]} ]}, {text:'button', children:[ {text:':text:demo button'} ]}, {text:'script', children:[ {text:':""src":"jquery.js"'} ]}, {text:'script', children:[ {text:':""src":"jstree.min.js"'} ]}, {text:'script', children:[ {text:':text:$(function ()'} ]} ]} ]} ]} ]} ]}
*/
function printJsonTreeData(){
    global $JsonTree;
    $res="";
    for($index=0; $index<count($JsonTree); $index++){
        $tag = $JsonTree[$index];
        $s="";
        if($index>0) {
            $diffLevel = $JsonTree[$index]->level - $JsonTree[$index-1]->level;
            $diffLevel /=2;
            if($diffLevel<0) {
                for($i=0; $i<(0-$diffLevel); $i++){
                    for($j=0; $j<($tag->level-$i); $j++) $s = $s . "  ";
                    if($i<(0-$diffLevel)-1) $s = $s . "]}\n";//"]}  "; //"]}\n";
                    else $s = $s . "]},\n";//"]},  "; //"]},\n";
                }
            }
        }

        for($i=0; $i<$tag->level; $i++) $s = $s . "  ";
        if(!$tag->hasChild) $tag->name = str_replace(":", ": ", $tag->name);
        $s = $s . "{text:'" . $tag->name . "'";
        if($tag->hasChild) $s = $s . ", children:[\n"; //", children:[  "; //", children:[\n";
        else {
            if($index < count($JsonTree)-1 && ($tag->level === $JsonTree[$index+1]->level)) $s = $s . "},\n"; //"},  "; //"},\n";
            else $s = $s . "},\n"; //"}  "; //"},\n";
        }

        if($index === count($JsonTree)-1) {
            for($i=0; $i < (int)($tag->level/2); $i++){
                for($j=0; $j<($tag->level-$i-1); $j++) $s = $s . "  ";
                $s = $s . "]}\n"; //"]}  "; //"]}\n";
            }
        }
        $res = $res .  $s;
    }
     
    return $res;
}

function writejsTreeData($jsTree, $jsTreeFile){
    $myfile = fopen($jsTreeFile, "w") or die("Unable to open file!");
    fwrite($myfile, $jsTree);
    fclose($myfile);
    print "jsTree data has been saved in " . $jsTreeFile."<br><br>";
}

function JSON2jsTreeData(){
    createJsonTreeData();
    $res = printJsonTreeData();
     
    $res = html_entity_decode($res);
    
    //Replacing multiple spaces with a single space:
    $res = preg_replace('/\s+/', ' ',$res);  // or $output = preg_replace('!\s+!', ' ', $input);
    
    $res = str_replace('"', "'", $res);
    
    return  $res;  
}
 

